// Requires popper.js
var popperInstance = null;
var tootelem = document.querySelectorAll(".tooltip-base");
var toot = document.querySelectorAll(".tooltip");
var showEvents = ["mouseenter", "focus"];
var hideEvents = ["mouseleave", "blur"];
function renderPopper() {
  for (var i = 0; i < tootelem.length; i++) {
    popperInstance = Popper.createPopper(tootelem[i], toot[i]);
  }
}
function show() {
  toot.style.setProperty("display", "block");
  renderPopper();
}
function hide() {
  toot.style.setProperty("display", "none");
  popperInstance.destroy();
  popperInstance = null;
}
showEvents.forEach(function(e) {
  for (var i = 0; i < tootelem.length; i++) {
    tootelem[i].addEventListener(e, show);
  }
});
hideEvents.forEach(function(e) {
  for (var i = 0; i < tootelem.length; i++) {
    tootelem[i].addEventListener(e, show);
  }
});
